public class MagicSquareInGrid {    public static int numMagicSquaresInside(int[][] grid) {        int rowLen = grid.length;        int colLen = grid[0].length;        int count = 0;        if(rowLen>10||rowLen<1||colLen>10||colLen<1) {            return count;        }        for( int i = 0; i<rowLen-2; i++) {            for( int j = 0; j<colLen-2; j++) {                if(grid[i][j]>9||grid[i][j]<1||grid[i][j+1]>9||grid[i][j+1]<1||grid[i][j+2]>9||grid[i][j+2]<1||grid[i+1][j]>9||grid[i+1][j]<1||grid[i+1][j+1]>9||grid[i+1][j+1]<1||grid[i+1][j+2]>9||grid[i+1][j+2]<1||grid[i+2][j]>9||grid[i+2][j]<1||grid[i+2][j+1]>9||grid[i+2][j+1]<1||grid[i+2][j+2]>9||grid[i+2][j+2]<1){                    continue;                }                if(grid[i][j]+grid[i][j+1]+grid[i][j+2] ==15 && (grid[i][j]+grid[i+1][j+1]+grid[i+2][j+2]) ==15 && (grid[i+1][j]+grid[i+1][j+1]+grid[i+1][j+2])==15 && (grid[i+2][j]+grid[i+2][j+1]+grid[i+2][j+2])== 15 && (grid[i][j]+grid[i+1][j]+grid[i+2][j])==15 && (grid[i][j+1]+grid[i+1][j+1]+grid[i+2][j+1])==15 && (grid[i][j+2]+grid[i+1][j+2]+grid[i+2][j+2])==15 && (grid[i][j+2]+grid[i+1][j+1]+grid[i+2][j])==15 ) {                    count++;                }            }        }        return count;    }    public static void main(String[] args) {        int[][] arr = {{7,7,7,9,1,4,6,7,2,5},{3,1,9,9,2,6,1,5,9,5},{8,6,3,7,1,8,8,3,4,4},{3,6,9,8,3,3,8,1,4,8},{4,4,1,6,3,2,4,6,5,4},{4,7,6,2,6,7,0,5,5,3},{8,2,5,3,4,3,8,9,3,1},{5,6,1,4,9,5,1,7,2,8},{0,4,8,1,2,7,6,6,3,5},{7,2,3,5,5,4,6,1,4,9}};        for (int i = 0; i<arr.length; i++) {            for(int j = 0; j<arr[0].length-1; j++) {                System.out.print(arr[i][j]+" ");            }            System.out.println(arr[i][arr[i].length-1]);        }        System.out.println(numMagicSquaresInside(arr));    }}