public class PlusOne {    public static int[] plusOne(int[] digits) {        for(int i = digits.length-1; i>=0; i--) {            if(digits[i]<9) {                digits[i]+=1;                return digits;            }            digits[i] = 0;        }        int[] res = new int[digits.length+1];        res[0] = 1;        for( int i = 1; i<res.length; i++) {            res[i] = 0;        }        return res;    }    public static void main(String [] args) {        int[] arr = {9,9,9,9};        int[] result = plusOne(arr);        for( int i = 0; i<result.length; i++) {            System.out.print(result[i]+" ");        }    }}