public class RemoveElement {    public static int removeElement(int[] nums, int val) {        int len = nums.length;        for ( int i = 0; i<len; i++) {            if(nums[i] == val) {                len -- ;                for(int j = i; j<len; j++) {                    nums[j] = nums[j+1];                }                i--;            }        }        return len;    }    public static void main(String[] args) {        int[] nums = {0,1,2,2,3,4,1,2};        int len = removeElement(nums,2);        for(int i = 0; i<len; i++) {            System.out.println(nums[i]);        }    }}