public class SearchInsertPosition {    public static int searchInsert(int[] nums, int target) {        int len = nums.length;        int index = 0;        if(nums[0]>target) {            return 0;        }        if(nums[len-1]<target){            return len;        }        for( int i = 0; i<len; i++) {            if(nums[i]==target||nums[i]>target) {                return i;            }        }        return index;    }    public static int searchBinary(int[] nums, int target) {        if(nums.length == 0) return 0;        int mid = 0;        int lo= 0,hi=nums.length-1;        while(lo<=hi){            mid = (lo+hi)/2;            if(nums[mid] == target) return mid;            else if(target > nums[mid]) lo = mid+1;            else hi = mid-1;        }        return target < nums[mid] ? mid : mid+1;    }    public static void main(String[] args) {        int[] nums = {1,3,5,7,9};        System.out.println(searchInsert(nums,12));    }}