public class SumMax {    public static int maxSubArray(int[] nums) {       int sumMax = nums[0];       int max = nums[0];       int start = 0;       int tempstart = 0;       int end = 0;       for(int i = 1; i<nums.length; i++) {           if(sumMax<0) {               sumMax = 0;               tempstart = i;           }           sumMax  += nums[i];           if(max<sumMax) {               max = sumMax;               start = tempstart;               end = i;           }       }       System.out.println(start+"," +end);       return max;    }    public static void main( String[] args) {        int[] nums = {-2,1,-3,4,-1,2,1,-5,4};        int[] nums2 = {2,3,1,-4,-1,-6,-7};        System.out.println(maxSubArray(nums));        System.out.println(maxSubArray(nums2));    }}